import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import "./Register.css"
import "react-toastify/dist/ReactToastify.css";
import { registerRoute } from "../utils/APIRoutes";

export default function Register() {

  const navigate = useNavigate();

  useEffect(() => {
    if (localStorage.getItem("secret-key-admin")) {
      navigate("/admin");
    } else if (localStorage.getItem("secret-key")) {
      navigate("/instructor");
    }
  }, []);

  const [RegisterValues, setValues] = useState({
    username: "",
    email: "",
    password: "",
    isAdmin: false,
  });



  const handleValidation = () => {
    const { password, username, email } = RegisterValues;    
      if (username.length < 3) {
      toast.error(
        "Username should be greater than 3 characters.",
        toastOptions
      );
      return false;
    } else if (password.length < 8) {
      toast.error(
        "Password should be equal or greater than 8 characters.",
        toastOptions
      );
      return false;
    } else if (email === "") {
      toast.error("Email is required.", toastOptions);
      return false;
    }
    return true;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (handleValidation()) {
      const { email, username, password } = RegisterValues;
      const { data } = await axios.post(registerRoute, {
        username,
        email,
        password,
        isAdmin: RegisterValues.isAdmin,
      });

      if (data.status === false) {
        toast.error(data.msg, toastOptions);
      }

      if (data.status === true) {
        if (data.user.isAdmin === true) {
          localStorage.setItem("secret-key-admin", JSON.stringify(data.user));
          navigate("/admin");
        } else if (data.user.isAdmin === false) {
          localStorage.setItem("secret-key", JSON.stringify(data.user));
          navigate("/instructor");
        }
      }
    }
  };

  const handleChange = (event) => {
    const { name, value, type, checked } = event.target;
    const newValue = type === "checkbox" ? checked : value;
    setValues({ ...RegisterValues, [name]: newValue });
  };

  return (
    <>
      <div className="form-container-register">
        <img
          src="https://toxicologycongress.wordpress.com/wp-content/uploads/2018/10/registernowgif.gif"
          alt="Register Now"
          className="registration-gif"
        />
        <div className="form-content-register">

          <form className="form-register" onSubmit={handleSubmit}>
            <input
              type="text"
              placeholder="Username"
              name="username"
              onChange={handleChange}
            />
            <input
              type="email"
              placeholder="Email"
              name="email"
              onChange={handleChange}
            />
            <input
              type="password"
              placeholder="Password"
              name="password"
              onChange={handleChange}
            />

            <div className="checkbox-container-register">
              <input
                type="checkbox"
                id="isAdmin"
                name="isAdmin"
                checked={RegisterValues.isAdmin}
                onChange={handleChange}
              />
              <label htmlFor="isAdmin">Admin</label>
            </div>
            <button className="button-register" type="submit">Create User</button>
            <span>
              Already have an account? <Link to="/login">Login.</Link>
            </span>
          </form>
        </div>
      </div>
      <ToastContainer />
    </>
  );
}
