import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { getCourse } from "../../utils/APIRoutes";
import axios from "axios";
import "../styles/CoursesAdmin.css"; // Ensure the path is correct

const CourseListPage = ({user}) => {
  const [courses, setCourses] = useState([]);

  const fetchCourses = async () => {
    try {
      const response = await axios.get(getCourse);
      setCourses(response.data.courses);
    } catch (error) {
      console.error("Error fetching courses:", error);
    }
  };

  useEffect(() => {
    fetchCourses();
  }, [courseData, user]);

  return (
    <div className="container">
      <div className="card-list">
        {courses.map((course) => (
          <div className="card" key={course._id}>
            <Link
              className="card-link"
              to={{
                pathname: `/individualcourse/${course._id}`, // Update this path
              }}
            >
              <img src={course.image} alt={course.name} />
              <div className="card-content">
                <h2>{course.name}</h2>
                <p>Level: {course.level}</p>
                <p>{course.description}</p>
              </div>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CourseListPage;
